CREATE TABLE `public`.`task` (
	`id` bigint AUTO_INCREMENT NOT NULL,
	`task_id` character varying(50 char) NOT NULL,
	`task_name` character varying(100 char) NOT NULL,
	`task_type` character varying(20 char) NOT NULL,
	`priority` character varying(10 char) NOT NULL,
	`description` text NULL,
	`executor` character varying(50 char) NOT NULL,
	`assistants` character varying(255 char) NULL,
	`planned_start_time` datetime NOT NULL,
	`planned_end_time` datetime NOT NULL,
	`actual_start_time` datetime NULL,
	`actual_end_time` datetime NULL,
	`inspection_line` character varying(50 char) NOT NULL,
	`start_location` character varying(50 char) NOT NULL,
	`end_location` character varying(50 char) NOT NULL,
	`inspection_scope` character varying(255 char) NOT NULL,
	`status` character varying(20 char) NOT NULL,
	`completion_rate` integer NULL,
	`execution_result` text NULL,
	`issues_found` integer NULL DEFAULT 0,
	`inspection_distance` double precision NULL,
	CONSTRAINT `task_completion_rate_check` CHECK (((completion_rate >= 0) AND (completion_rate <= 100))),
	CONSTRAINT `task_pkey` PRIMARY KEY (id),
	CONSTRAINT `task_task_id_key` UNIQUE (task_id)
) TABLESPACE sys_default AUTO_INCREMENT = 1;



